#import "ShareViewController.h"

@interface ShareViewController (){
   
}

@end

@implementation ShareViewController

- (id)initWithNibName:(NSString *)nibNameOrNil bundle:(NSBundle *)nibBundleOrNil
{
    self = [super initWithNibName:nibNameOrNil bundle:nibBundleOrNil];
    if (self) {
        // Custom initialization
    }
    return self;
}


- (void)viewDidLoad
{
    [super viewDidLoad];
    // Do any additional setup after loading the view.
    self.navigationController.navigationBar.tintColor=[UIColor blackColor];
    UIButton *shareButton = [[UIButton alloc]initWithFrame:CGRectMake(50, 200, 220, 50)];
    [shareButton setTitle:@"分享给好友" forState:UIControlStateNormal];
    shareButton.backgroundColor=[UIColor redColor];
    [shareButton addTarget:self action:@selector(shareContent:) forControlEvents:UIControlEventTouchUpInside];
    [self.view addSubview:shareButton];
    
}

-(void)shareContent:(UIButton *)sender{
    NSString *imagePath = [[NSBundle mainBundle] pathForResource:@"ShareSDK"  ofType:@"jpg"];

    
    //构造分享内容
    id<ISSContent> publishContent = [ShareSDK content:@"金手指Pro"
                                       defaultContent:@"这个应用很好玩，快来下载吧"
                                                image:[ShareSDK imageWithPath:imagePath]
                                                title:@"金手指"
                                                  url:@"http://www.sina.cn"
                                          description:@"这是一条测试信息"
                                            mediaType:SSPublishContentMediaTypeNews];
    //定制微信朋友圈信息
    [publishContent addWeixinTimelineUnitWithType:[NSNumber numberWithInteger:SSPublishContentMediaTypeMusic]
                                          content:INHERIT_VALUE
                                            title:NSLocalizedString(@"金手指Pro", @"Hello 微信朋友圈!")
                                              url:@"http://y.qq.com/i/song.html#"
                                       thumbImage:[ShareSDK imageWithUrl:@"http://img1.bdstatic.com/img/image/67037d3d539b6003af38f5c4c4f372ac65c1038b63f.jpg"]
                                            image:INHERIT_VALUE
                                     musicFileUrl:@"http://mp3.mwap8.com/destdir/Music/2009/20090601/ZuiXuanMinZuFeng20090601119.mp3"
                                          extInfo:nil
                                         fileData:nil
                                     emoticonData:nil];

    //创建自定义分享列表
    NSArray *shareList =[ShareSDK getShareListWithType:
                          ShareTypeSinaWeibo,
                         ShareTypeQQSpace,
                         ShareTypeWeixiSession,
                         ShareTypeWeixiTimeline,
                         ShareTypeQQ,nil
                         ];
    [ShareSDK showShareActionSheet:nil
                         shareList:shareList
                           content:publishContent
                     statusBarTips:YES
                       authOptions:nil
                      shareOptions: nil
                            result:^(ShareType type, SSResponseState state, id<ISSPlatformShareInfo> statusInfo, id<ICMErrorInfo> error, BOOL end) {
                                if (state == SSResponseStateSuccess)
                                {
                                    NSLog(@"分享成功");
                                }
                                else if (state == SSResponseStateFail)
                                {
                                    NSLog(@"分享失败,错误码:%d,错误描述:%@", [error errorCode], [error errorDescription]);
                                }
                            }];}
- (void)didReceiveMemoryWarning
{
    [super didReceiveMemoryWarning];
    // Dispose of any resources that can be recreated.
}

